import React from "react";
import Skill from "./Skill.js";

const myStyle = {
    stroke: "rgb(255,255,0)",
    strokeWidth: 5,
    zIndex: 1,
}
const svgStyle = {
    position: "absolute",
    // backgroundColor: "green",
    opacity: 0.4,
    zIndex: 1,
    width: "100%",
    minHeight: "35vh",
}
export default class extends React.Component {
    state = {
        cards: [{
            key: 0,
            title: "Web Development",
            skills: [{ key: 0, value: "React" }, { key: 1, value: "SCSS" }],
        },
        {
            key: 1,
            title: "Personal",
            skills: [{ key: 0, value: "Loving Jaymee" }],
        },
        {
            key: 2,
            title: "Anotha One",
            skills: [{ key: 0, value: ["being big op"] }],
        },
        ],
        coordinates:[],
    }
    componentDidMount() {
        this.updatePositionOfObjects();
        window.addEventListener('resize',this.updatePositionOfObjects);
    }

    updatePositionOfObjects=()=>{
        const aboutMargin = 75;
        const cardWidth= 150;
        var rect = document.getElementsByClassName('MuiCard-root');
        this.setState({coordinates:[]});
        Array.from(rect).forEach((element,i) => {
            this.setState({coordinates:[
                ...this.state.coordinates,
                {
                    key:i,
                    x:element.getBoundingClientRect().x-aboutMargin+cardWidth,
                    y:element.getBoundingClientRect().y,
                }
            ]})
        });
        console.log(this.state);
    }

    drawTree=()=>{
        const lines = this.state.coordinates.map(x=>(
            <svg style={svgStyle} key={x.key}>
                <line x1={x.x} y1="0" x2={x.x} y2={x.y}
                    style={myStyle}
                />
            </svg>
        ));
        return lines;
    }
    render() {
        const lines = this.drawTree();
        return (
            <>
            {lines}
            <div className="container">
                {this.state.cards.map(x => (
                    <Skill 
                        key={x.key}
                        title={x.title}
                        skills={x.skills}
                    />
                ))}
            </div>
            </>
        );
    }
}
