import React, {useEffect} from "react";

export default function Timer(props){
    const timeUntil = props.birthday - props.currentTime;
    const [time, setTime] = React.useState(timeUntil);

    const updateTime=()=>{
        setInterval(()=>setTime(time - 1000),1);
    }
    useEffect(()=>{
        updateTime();
    })
    return(
        <p>
            {time}
        </p>
    );
}
