    getSkillObjectFromState=(name)=>{
        const val= this.state.cards.map(x=>
            x.skills.filter(element => 
                element.value === name
            )
        );
        return val[0][0];
    }
    getSkillCard=(key)=>{
        console.log(key);
        return this.state.cards[key];
    }
    clearActive=(key)=>{
        const cardSelected=this.state.cards[key].active;
        this.setState({[cardSelected]:{}})
        console.log(cardSelected);
    }
    getCardIndex=(obj)=>{
        const selectedCard = this.state.cards.filter((x,i)=>(
            console.log(x),
            x.skills.includes(obj)
        ));
        return selectedCard.key;
    }
    updateActive=(name)=>{
        const skillObject = this.getSkillObjectFromState(name);
        const skillIndex = skillObject.key;

        const cardIndex = this.getCardIndex(skillObject);
        // this.clearActive(key);

        let newState = Object.assign({},this.state);
        // newState.cards[key].active = skillObject;
        // const card = this.state.cards[key];
        this.setState(newState);
    }
    render() {
        const card = this.state.cards.map((skill,i)=>(
            <SkillContainerV1
                key={i}
                title={skill.title}
                skills={skill.skills}
                active={skill.active}
                updateActive={this.updateActive}
            />
        ));
        return (

            <div className ="cardContainer">
                    {card}
            </div>
        );
    }
}
