import React from "react";
import Skill from "./Skill.js";

export default class extends React.Component {
    state = {
        cards: [{
            key: 0,
            title: "Web Development",
            skills: [{ key: 0, value: "React" }, { key: 1, value: "SCSS" }],
        },
        {
            key: 1,
            title: "Personal",
            skills: [{ key: 0, value: "Loving Jaymee" }],
        },
        {
            key: 2,
            title: "Anotha One",
            skills: [{ key: 0, value: ["being big op"] }],
        },
        ],
        pos: [],
    }

    componentDidMount() {
        var rect = document.getElementsByClassName('MuiCard-root');
        const position = {
            x: 0,
            y: 0,
        }
        let coordinatesOfCards = [];
        Array.from(rect).forEach(element => {
            position.x = element.getBoundingClientRect().x;
            position.y = element.getBoundingClientRect().y;
            coordinatesOfCards = coordinatesOfCards.concat(position);
        });
        // this.setState({pos:coordinatesOfCards})
            // .then(console.log(this.state));
        this.drawTree(coordinatesOfCards);
    }
    drawTree=(arrayOfCards)=>{
        const lines = arrayOfCards.map(mid=>{
            // <svg style={svgStyle}>
            //     <line x1="100" y1="0" x2="100" y2="100"
            //         style={myStyle}
            //     />
            // </svg>
        })
    }
    render() {
        const myStyle = {
            stroke: "rgb(255,255,0)",
            strokeWidth: 5,
            zIndex: 1,
        }
        const svgStyle = {
            position: "absolute",
            backgroundColor: "green",
            opacity: 0.4,
            zIndex: 1,
            width: "100%",
            minHeight: "35vh",
        }
        return (
            <>
            <svg style={svgStyle}>
                <line x1="150" y1="0" x2="150" y2="200"
                    style={myStyle}
                />
            </svg>
            <div className="container">
                {this.state.cards.map(x => (
                    <Skill title={x.title}
                        skills={x.skills}
                    />
                ))}
            </div>
            </>
        );
    }
}
