import React from "react";
import "./Styling/Styling.css";
import Heart from "./SVGS/heart.js";
import Writing from "./Writing/Writing.json";
import Timer from "./Timer.js"

let today = new Date();
let birthday = new Date(2020, 3, 29, 3, 0, 0);
export default class extends React.Component {
    state = {
        celebrate: false,
    }
    render() {
        const checkIfBirthday = () => {
            return today.getDay === birthday.getDay() ? true : false;
        }
        if (checkIfBirthday) {
            return (
                <>
                <div id="title">I love jaymee</div>
                <Heart />
                <p>{Writing.intro}</p>
                </>
            );
        }
        else {
            return (
                <>
                <Timer
                    currentTime={today}
                    birthday={birthday}
                />
                </>
            );
        }
    }
}
