import React from "react";
import SkillContainerV1 from "./SkillContainerV1.js";
import "./CardStyling.css";
export default class extends React.Component {
    state = {
        cards: [{
            key: 0,
            title: "Web Development",
            skills: [{ key: 0, value: "React", content:"blah"}, { key: 1, value: "SCSS",content :"aha" }],
            active:{}
        },
        {
            key: 1,
            title: "Personal",
            skills: [{ key: 0, value: "Loving Jaymee" , content:"and Jaymee loves me"}],
            active:{}
        },
        {
            key: 2,
            title: "Anotha One",
            skills: [{ key: 0, value: "being big op", content:"biiiig op"}],
            active:{},
        },
        ],
    }
    //look into if there's a better way of doing this?
    getSkillObjectFromState=(name)=>{
        const val= this.state.cards.map(x=>
            x.skills.filter(element => 
                element.value === name
            )
        );
        return val[0][0];
    }
    getSkillCard=(key)=>{
        console.log(key);
        return this.state.cards[key];
    }
    clearActive=(key)=>{
        const cardSelected=this.state.cards[key].active;
        this.setState({[cardSelected]:{}})
        console.log(cardSelected);
    }
    updateActive=(name)=>{
        const skillObject = this.getSkillObjectFromState(name);
        const cardIndex = skillObject.key;
        this.clearActive(cardIndex);

        const activeCardSkill = {
            ...this.state.cards[cardIndex],
            active:skillObject
        };
        const cardToUpdate = this.state.cards[cardIndex];
        console.log(cardToUpdate);
        this.setState({cardToUpdate: skillObject},()=>{console.log(this.state.cards[cardIndex])});
        // console.log(this.state.cards[skillIndex]);
    }
    render() {
        const card = this.state.cards.map((skill,i)=>(
            <SkillContainerV1
                key={i}
                title={skill.title}
                skills={skill.skills}
                active={skill.active}
                updateActive={this.updateActive}
            />
        ));
        return (

            <div className ="cardContainer">
                    {card}
            </div>
        );
    }
}
