import React from "react";
import Skill from "./Skill.js";

const myStyle = {
    stroke: "rgb(255,255,0)",
    strokeWidth: 5,
    zIndex: 1,
}
const svgStyle = {
    position: "absolute",
    // backgroundColor: "green",
    opacity: 0.4,
    zIndex: 1,
    width: "100%",
    minHeight: "35vh",
}
export default class extends React.Component {
    state = {
        cards: [{
            key: 0,
            title: "Web Development",
            skills: [{ key: 0, value: "React" }, { key: 1, value: "SCSS" }],
        },
        {
            key: 1,
            title: "Personal",
            skills: [{ key: 0, value: "Loving Jaymee" }],
        },
        {
            key: 2,
            title: "Anotha One",
            skills: [{ key: 0, value: ["being big op"] }],
        },
        ],
        coordinates:[],
    }
    componentDidMount() {
        this.updatePositionOfObjects();
        window.addEventListener('resize',this.updatePositionOfObjects);
    }

    updatePositionOfObjects=()=>{
        var rect = document.getElementsByClassName('MuiCard-root');
        const position = {
            x: 0,
            y: 0,
        }
        let coordinatesOfCards = [];
        Array.from(rect).forEach(element => {
            position.x = element.getBoundingClientRect().x;
            position.y = element.getBoundingClientRect().y;
            coordinatesOfCards = coordinatesOfCards.concat(position);
        });
        console.log(coordinatesOfCards);
        this.setState({coordinates:coordinatesOfCards});
    }

    drawTree=()=>{
        const lines = this.state.coordinates.map(x=>(
            <svg style={svgStyle}>
                <line x1={x.x} y1="0" x2={x.x} y2={x.y}
                    style={myStyle}
                />
            </svg>
        ));
        return lines;
    }
    render() {
        const lines = this.drawTree();
        return (
            <>
            {lines}
            <div className="container">
                {this.state.cards.map(x => (
                    <Skill title={x.title}
                        skills={x.skills}
                    />
                ))}
            </div>
            </>
        );
    }
}
