document.addEventListener(ready, () => {
  const words = [
    "Software Engineer",
    "Front End Developer",
    "Back End Developer",
    "Gym Enthusiast",
    "Jaymee's Lover",
  ];
  let index = 0;
  let wordIndex = 0;
  let currentText = "";
  let letter = "";

  (function type() {
    if (index === words.length) {
      index = 0;
    }
    currentText = words[index];
    letter = currentText.slice(0, ++wordIndex);

    //   document.querySelector(".role").textContent = letter;
    console.log(document.querySelector(".role"));
    if (letter.length === currentText.length) {
      index++;
      wordIndex = 0;
    }
    setTimeout(type, 4000);
  })();
});
