import React from "react";
import Blog from "./Blog.js";
import BlogAdmin from "./BlogAdmin.js";

export default class extends React.Component {
    state = {
        posts: []
    }
    componentDidMount() {
        fetch('/api/blog/')
            .then(res=>{return res.json()})
            .then(data=>this.setState({posts:data}));
    }
    createNewPost=(postObject)=>{
        const allPosts = [ ...this.state.posts, postObject];
        const lastId=this.state.posts;
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(postObject)
        };
        fetch('api/blog/add', requestOptions)
            .then(response => this.setState({posts:allPosts}));
    }
    deletePost=(post_id)=>{
        const index = this.state.posts.findIndex(x => x.id === post_id);
        console.log(index);
        // const newState = {
        //     ...this.state.posts.splice(0,)
        // }
        const requestOptions={
            method:'DELETE',
            headers:{'Content-type':'application/json'},
            body:JSON.stringify({id:post_id})
        }
        fetch('api/blog',requestOptions)
            .then(response=>response.json())
    }
    render() {
        const posts = this.state.posts.map((post,i) =>
                <Blog
                    key={i}
                    id={post.id}
                    title={post.title}
                    content={post.content}
                    deletePost={this.deletePost}
                />
            );
        return (
            <>
                {posts}
                <BlogAdmin
                    createNewPost={this.createNewPost}
                />
            </>
        );
    }
}
