import React from "react";
import Blog from "./Blog.js";
import BlogAdmin from "./BlogAdmin.js";

export default class extends React.Component {
    state = {
        posts: []
    }
    componentDidMount() {
        fetch('/api/blog/')
            .then(res=>{return res.json()})
            .then(data=>this.setState({posts:data}));
    }
    getLastIndexOfPosts=()=>{
        return this.state.posts[this.state.posts.length-1]
    }
    createNewPost=(postObject)=>{
        const lastId = this.getLastIndexOfPosts().id;
        const newObject = {
            ...postObject,
            id:lastId+1
        }
        const allPosts = [ ...this.state.posts, newObject];
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(postObject)
        };
        fetch('api/blog/add', requestOptions)
            .then(response => this.setState({posts:allPosts}));
    }
    deletePost=(post_id)=>{
        // const index = this.state.posts.findIndex(x => x.id === post_id);

        console.log(index);
        const newState = this.state.filter(x=>x.id !== post_id);
        const requestOptions={
            method:'DELETE',
            headers:{'Content-type':'application/json'},
            body:JSON.stringify({id:post_id})
        }
        fetch('api/blog',requestOptions)
            .then(response=>this.setState({newState}))
    }
    render() {
        const posts = this.state.posts.map((post,i) =>
                <Blog
                    key={i}
                    id={post.id}
                    title={post.title}
                    content={post.content}
                    deletePost={this.deletePost}
                />
            );
        return (
            <>
                {posts}
                <BlogAdmin
                    createNewPost={this.createNewPost}
                />
            </>
        );
    }
}
