import React from "react";
import "./Blog.css";
export default class extends React.Component{
    state={
        postDetails:{
            title:"",
            content:"",
        }
    }
    updateValue = (e) =>{
        poop = e.target.name;
        this.setState({poop: e.target.value});
    }
    handleChange=()=>{

        e.preventDefault();
    }
    render(){
        return(
            <div className="BlogInput">
                <form>
                    <label>Title:<input type="text" name = "title" value={this.state.title} onChange={this.updateValue}/></label>
                    <br/>
                    <label>Content:<input type="text" name="content" value = {this.state.content} onChange={this.updateValue}/></label>
                    <br/>
                    <input type="submit" value="Submit" onClick={this.handleChange()}/>
                </form>
            </div>
        );
    }
}
