import React from "react";
import Blog from "./Blog.js";
import BlogAdmin from "./BlogAdmin.js";

export default class extends React.Component {
    state = {
        posts: []
    }
    componentDidMount() {
        fetch('/api/blog/posts')
            .then(res=>{return res.json()})
            .then(data=>this.setState({posts:data}));
    }
    createNewPost=(postObject)=>{
        const allPosts = [ ...this.state.posts, postObject];
        this.setState({
            posts:allPosts
        });
        fetch(`/api/blog/add?title=${postObject.title}&content=${postObject.content}`)
            .then(response=>response.json())
            .then(data=>console.log(data));
        // const requestOptions={
        //     method:'POST',
        //     headers:{'Content-type':'application/json'},
        //     body:JSON.stringify({...postObject})
        // };
        // fetch('/api/blog/add',requestOptions)
        //     .then(response=>response.json())
        //     .then(data=>console.log(data));
        console.log(allPosts);
    }
    render() {
        const posts = this.state.posts.map((post,i) =>
                <Blog
                    key={i}
                    title={post.title}
                    content={post.content}
                />
            );
        return (
            <>
                {posts}
                <BlogAdmin
                    createNewPost={this.createNewPost}
                />
            </>
        );
    }
}
